import React, { useState } from "react";

function App() {
  const [activeTab, setActiveTab] = useState("submit");

  return (
    <div className="min-h-screen bg-gray-100 font-sans">
      {/* Navbar */}
      <nav className="bg-white shadow p-4 flex justify-between items-center">
        <h1 className="text-xl font-bold text-green-600">Mini E-Commerce Platform</h1>
        <div className="space-x-4">
          <button
            className={`px-4 py-2 rounded-lg font-medium ${
              activeTab === "submit"
                ? "bg-green-500 text-white"
                : "bg-gray-200 text-gray-700"
            }`}
            onClick={() => setActiveTab("submit")}
          >
            Submit Product
          </button>
          <button
            className={`px-4 py-2 rounded-lg font-medium ${
              activeTab === "view"
                ? "bg-green-500 text-white"
                : "bg-gray-200 text-gray-700"
            }`}
            onClick={() => setActiveTab("view")}
          >
            View Products
          </button>
        </div>
      </nav>

      {/* Content */}
      <main className="p-6">
        {activeTab === "submit" ? (
          <div className="bg-white p-6 rounded shadow">
            <h2 className="text-lg font-semibold mb-4">Submit a New Product</h2>
            {/* Product submission form will go here */}
            <p className="text-gray-500">[Product submission form placeholder]</p>
          </div>
        ) : (
          <div className="bg-white p-6 rounded shadow">
            <h2 className="text-lg font-semibold mb-4">View Submitted Products</h2>
            {/* Product listing will go here */}
            <p className="text-gray-500">[Product listing placeholder]</p>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
